<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'administration/includes/auth.php';/* objects. */require_once 'class/_comm.php';$commObject 	= new class_comm();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$commData = $commObject->getByCode($code);	if(!$commData) {		header('Location: /administration/comms/');		exit;	}	$smarty->assign('commData', $commData);} else {	header('Location: /administration/comms/');	exit;}$smarty->display('administration/communication/details.tpl');?>