<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'administration/includes/auth.php';require_once 'class/profilecategory.php';$profilecategoryObject = new class_profilecategory();if(isset($_GET['delete_code'])) {		$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success					= NULL;	$code						= trim($_GET['delete_code']);			if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {		$data	= array();		$data['profilecategory_deleted'] = 1;				$where = $profilecategoryObject->getAdapter()->quoteInto('profilecategory_code = ?', $code);		$success	= $profilecategoryObject->update($data, $where);					if(is_numeric($success) && $success > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}		echo json_encode($errorArray);	exit;}/* Setup Pagination. */$profilecategoryData = $profilecategoryObject->getAll("profilecategory_deleted = 0",'profilecategory.profilecategory_added DESC');if($profilecategoryData) $smarty->assign_by_ref('profilecategoryData', $profilecategoryData);/* End Pagination Setup. */$smarty->display('administration/participant/profile/category/default.tpl');?>