<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'administration/includes/auth.php';/* objects. */require_once 'class/profilecategory.php';$profilecategoryObject 	= new class_profilecategory();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$profilecategoryData = $profilecategoryObject->getByCode($code);		if(!$profilecategoryData) {		header('Location: /administration/participant/view/');		exit;	}	$smarty->assign('profilecategoryData', $profilecategoryData);}/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;		if(isset($_POST['profilecategory_name']) && trim($_POST['profilecategory_name']) == '') {		$errorArray['profilecategory_name'] = 'required';		$formValid = false;			}		if(isset($_POST['profilecategory_description']) && trim($_POST['profilecategory_description']) == '') {		$errorArray['profilecategory_description'] = 'required';		$formValid = false;			}			if(count($errorArray) == 0 && $formValid == true) {				$data 	= array();						$data['profilecategory_name']				= trim($_POST['profilecategory_name']);							$data['profilecategory_description']		= trim($_POST['profilecategory_description']);								if(isset($profilecategoryData)) {					/*Update. */			$where		= $profilecategoryObject->getAdapter()->quoteInto('profilecategory_code = ?', $profilecategoryData['profilecategory_code']);			$success	= $profilecategoryObject->update($data, $where);											} else {									$success = $profilecategoryObject->insert($data);					}				if(count($errorArray) == 0) {										header('Location: /administration/participant/profile/category/');				exit;				}				}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('administration/participant/profile/category/details.tpl');?>