<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'administration/includes/auth.php';require_once 'class/profile.php';$profileObject = new class_profile();  if(isset($_GET['delete_code'])) {		$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success					= NULL;	$code						= trim($_GET['delete_code']);			if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {		$data	= array();		$data['profile_deleted'] = 1;				$where = $profileObject->getAdapter()->quoteInto('profile_code = ?', $code);		$success	= $profileObject->update($data, $where);					if(is_numeric($success) && $success > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}		echo json_encode($errorArray);	exit;}/* Setup Pagination. */$profileData = $profileObject->getAll('profile_deleted = 0','profile.profile_added');if($profileData) $smarty->assign_by_ref('profileData', $profileData);/* End Pagination Setup. */$smarty->display('administration/participant/profile/view/default.tpl');?>