<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'administration/includes/auth.php';/* objects. */require_once 'class/profile.php';require_once 'class/File.php';$profileObject 				= new class_profile();$fileObject 					= new File(array('gif', 'png', 'jpg', 'jpeg', 'gif'));if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$profileData = $profileObject->getByCode($code);		if($profileData) {		$smarty->assign('profileData', $profileData);	} else {		header('Location: /administration/participant/profile/view/');		exit;			}}/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;	$areaByName	= NULL;		if(isset($_POST['areapost_code']) && trim($_POST['areapost_code']) == '') {		$errorArray['areapost_code'] = 'required';		$formValid = false;			}		if(isset($_POST['profile_name']) && trim($_POST['profile_name']) == '') {		$errorArray['profile_name'] = 'required';		$formValid = false;			}		if(isset($_POST['profile_surname']) && trim($_POST['profile_surname']) == '') {		$errorArray['profile_surname'] = 'required';		$formValid = false;			}		if(isset($_POST['profile_dateofbirth']) && trim($_POST['profile_dateofbirth']) != '') {		if(trim($_POST['profile_dateofbirth']) != date('Y-m-d', strtotime(trim($_POST['profile_dateofbirth'])))) {			$errorArray['profile_dateofbirth'] = 'required';			$formValid = false;				}	}		if(isset($_POST['profile_idnumber']) && trim($_POST['profile_idnumber']) != '') {		if($profileObject->validateIDnumber(trim($_POST['profile_idnumber'])) == '') {			$errorArray['profile_idnumber'] = 'Needs to be a valid id number';			$formValid = false;			} else {						$idnumber = isset($profileData) ? $profileData['profile_code'] : null;						$idnumberData = $profileObject->getByIDnumber(trim($_POST['profile_idnumber']), $idnumber);						if($idnumberData) {				$errorArray['profile_idnumber'] = 'ID number already exists';				$formValid = false;							} else {				if((isset($_POST['profile_dateofbirth']) && trim($_POST['profile_dateofbirth']) != '') && (isset($_POST['profile_idnumber']) && trim($_POST['profile_idnumber']) != '')) {					if(date('ymd', strtotime(trim($_POST['profile_dateofbirth']))) != substr(trim($_POST['profile_idnumber']), 0, 6)) {						$errorArray['profile_idnumber'] = 'ID number should relate to the date of birth';						$formValid = false;					}				}						}		}	}		if(isset($_POST['profile_email']) && trim($_POST['profile_email']) != '') {		if($profileObject->validateEmail(trim($_POST['profile_email'])) == '') {			$errorArray['profile_email'] = 'Needs to be a valid email address';			$formValid = false;			} else {						$email = isset($profileData) ? $profileData['profile_code'] : null;						$emailData = $profileObject->getByEmail(trim($_POST['profile_email']), $email);						if($emailData) {				$errorArray['profile_email'] = 'Email already exists';				$formValid = false;							}		}	} else {		$errorArray['profile_email'] = 'Please add an email address';		$formValid = false;					}		if(isset($_POST['profile_cellphone']) && trim($_POST['profile_cellphone']) != '') {		if($profileObject->validateCell(trim($_POST['profile_cellphone'])) == '') {			$errorArray['profile_cellphone'] = 'Needs to be a valid cellphone';			$formValid = false;			} else {						$cell = isset($profileData) ? $profileData['profile_code'] : null;						$cellData = $profileObject->getByCell(trim($_POST['profile_cellphone']), $cell);						if($cellData) {				$errorArray['profile_cellphone'] = 'Cell phone number already exists';				$formValid = false;							}		}	} else {		$errorArray['profile_cellphone'] = 'Please add a cellphone number';		$formValid = false;					}		if(isset($_POST['profile_telephone']) && trim($_POST['profile_telephone']) != '') {		if($profileObject->validateCell(trim($_POST['profile_telephone'])) == '') {			$errorArray['profile_telephone'] = 'Needs to be a valid telephone number';			$formValid = false;			}	}		if(isset($_FILES['profile_image'])) {		/* Check validity of the CV. */		if((int)$_FILES['profile_image']['size'] != 0 && trim($_FILES['profile_image']['name']) != '') {			/* Check if its the right file. */			$ext = array_search($_FILES['profile_image']['type'], $fileObject->mime_types); 						if($ext != '') {				if(!$fileObject->valideExt($ext)) { 					$errorArray['profile_image'] = 'Invalid file type';					$formValid = false;										}			} else {				$errorArray['profile_image'] = 'Invalid file type';				$formValid = false;												}		}	}			if(count($errorArray) == 0 && $formValid == true) {				$data 	= array();						$data['areapost_code']			= trim($_POST['areapost_code']);				$data['profile_name']				= trim($_POST['profile_name']);				$data['profile_surname']			= trim($_POST['profile_surname']);				$data['profile_dateofbirth']		= trim($_POST['profile_dateofbirth']);				$data['profile_gender']			= trim($_POST['profile_gender']);				$data['profile_gender']			= trim($_POST['profile_gender']);			$data['profile_email']				= trim($_POST['profile_email']);				$data['profile_cellphone']		= trim($_POST['profile_cellphone']);				$data['profile_telephone']		= trim($_POST['profile_telephone']);					$data['profile_idnumber']		= trim($_POST['profile_idnumber']);					$data['profile_position']			= trim($_POST['profile_position']);				$data['profile_description']				= trim($_POST['profile_description']);				$data['profile_social_website']			= trim($_POST['profile_social_website']);				$data['profile_social_twitter']			= trim($_POST['profile_social_twitter']);				$data['profile_social_facebook']		= trim($_POST['profile_social_facebook']);				$data['profile_social_googleplus']	= trim($_POST['profile_social_googleplus']);				$data['profile_social_linkedin']		= trim($_POST['profile_social_linkedin']);				$data['profile_social_whoswho']		= trim($_POST['profile_social_whoswho']);							if(isset($profileData)) {						$data['profile_code'] = $profileData['profile_code'];						/*Update. */			$where		= $profileObject->getAdapter()->quoteInto('profile_code = ?', $profileData['profile_code']);			$success	= $profileObject->update($data, $where);				$success	= $data['profile_code'];					} else {						$success = $profileObject->insert($data, true);					}				/* Upload image if its added. */		if((int)$_FILES['profile_image']['size'] != 0 && trim($_FILES['profile_image']['name']) != '') {						$image = array();			$image['profile_image_name']	= $profileObject->createFile();			$image['profile_image_path']		= '';			$image['profile_image_ext']		= '';						$ext 						= strtolower($fileObject->file_extention($_FILES['profile_image']['name']));								$filename				= $image['profile_image_name'].'.'.$ext;						$directory				= $_SERVER['DOCUMENT_ROOT'].'/media/profile/'.$success.'/';			$file						= $directory.'/'.$filename;							if(!is_dir($directory)) mkdir($directory, 0777, true);			/* Create files for this product type. */			foreach($fileObject->image as $item) {								$newfilename = str_replace($filename, $item['code'].$filename, $file);								/* Create new file and rename it. */				$uploadObject	= PhpThumbFactory::create($_FILES['profile_image']['tmp_name']);				$uploadObject->resize($item['width'], $item['height']);				$uploadObject->save($newfilename);						}			$image['profile_image_path']	= '/media/profile/'.$success.'/';			$image['profile_image_ext']	= '.'.$ext;			$image['profile_code'] 			= $success;						$where = $profileObject->getAdapter()->quoteInto('profile_code = ?', $success);			$profileObject->update($image, $where);							}				if(count($errorArray) == 0) {			header('Location: /administration/participant/profile/view/page.php?code='.$success);				exit;				}					}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('administration/participant/profile/view/details.tpl');?>